import React, { useState, useEffect } from 'react';
import '../styles/L';

const LoginForm = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const validate = () => {
    if (username === password && username.length > 1) {
      alert('Login Successful');
      window.localStorage.setItem('loginStatus', 'true');
      window.location.href = 'orders.html';
    } else {
      alert('Please enter valid credentials!');
    }
  };

  useEffect(() => {
    const status = window.localStorage.getItem('loginStatus');
    if (status === 'true') {
      window.location.href = 'orders.html';
    }
  }, []);

  return (
    <div className="maincontent">
      <form className="LoginPage_LoginForm__3_wtJ" name="myform">
        <h1>Sign In</h1>
        <input
          className="LoginPage_InputField__2lu1x"
          type="text"
          name="username"
          placeholder="Enter Username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />
        <input
          className="LoginPage_InputField__2lu1x"
          type="password"
          name="password"
          placeholder="Enter Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <input
          className="LoginPage_Button__qkLR7"
          type="button"
          value="Login"
          onClick={validate}
        />
      </form>
    </div>
  );
};

export default LoginForm;
