import React, { useEffect, useState } from 'react';
import axios from 'axios';
import '../styles/style.css'; // Import your CSS file

const Users = () => {
  const [searchValue, setSearchValue] = useState('');
  const [users, setUsers] = useState([]);

  useEffect(() => {
    getUsers(); // Fetch users data on component mount
  }, []);

  const getUsers = () => {
    axios
      .get("https://5fc1a1c9cb4d020016fe6b07.mockapi.io/api/v1/users")
      .then(function (response) {
        const mydatas = response.data;
        setUsers(mydatas);
      })
      .catch(function (error) {
        console.log(error);
      });
  };

  const handleSearchChange = (event) => {
    const { value } = event.target;
    setSearchValue(value);
  };

  const resetSearch = () => {
    setSearchValue('');
    getUsers();
  };

  const filteredUsers = users.filter(user => {
    return user.fullName.toLowerCase().includes(searchValue.toLowerCase());
  });

  return (
    <div className="maincontainer">
      <h1>Users</h1>

      <form className="UserList_FilterWrapper">
        <input
          className="UserList_SearchBox"
          type="search"
          id="search-box"
          placeholder="Search by Name"
          value={searchValue}
          onChange={handleSearchChange}
        />
        <input type="button" onClick={resetSearch} className="UserList_Button" value="Reset" />
      </form>

      <div className="Homepageorders">
        <table className="Homepage_OrderTable">
          <thead>
            <tr>
              <th>ID</th>
              <th>User Avatar</th>
              <th>Full Name</th>
              <th>DoB</th>
              <th>Gender</th>
              <th>Current Location</th>
            </tr>
          </thead>
          <tbody>
            {filteredUsers.map(user => (
              <tr key={user.id}>
                <td>{user.id}</td>
                <td><img src={user.profilePic} alt="avatar" /></td>
                <td>{user.fullName}</td>
                <td>{user.dob}</td>
                <td>{user.gender}</td>
                <td>{`${user.currentCity} ${user.currentCountry}`}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Users;
