import React, { useEffect } from 'react';
import axios from 'axios';
import '../styles/style.css'; // Import your CSS file

const Users = () => {

  useEffect(() => {
    getUsers(); // Fetch users data on component mount
    addSearchListener(); // Add search listener on component mount
  }, []);

  const getUsers = () => {
    axios
      .get("https://5fc1a1c9cb4d020016fe6b07.mockapi.io/api/v1/users")
      .then(function (response) {
        const mydatas = response.data;
        buildTableData(mydatas);
      })
      .catch(function (error) {
        console.log(error);
      });
  };

  const buildTableData = (data) => {
    let temp = "";
    data.forEach((itemData) => {
      temp += "<tr >";
      temp += "<td>" + itemData.id + "</td>";
      temp += "<td>" + `<img src= {itemData.profilePic} alt="avatar" />` + "</td>";
      temp += "<td>" + itemData.fullName + "</td>";
      temp += "<td>" + itemData.dob + "</td>";
      temp += "<td>" + itemData.gender + "</td>";
      temp += "<td>" + itemData.currentCity + " " + itemData.currentCountry + "</td></tr>";
    });
    document.getElementById("mytabledata").innerHTML = temp;
  };

  const addSearchListener = () => {
    $("#search-box").on("keyup", function () {
      const value = $(this).val();
      if (value.length >= 2) {
        axios
          .get(`https://5fc1a1c9cb4d020016fe6b07.mockapi.io/api/v1/users?fullName=${value}`)
          .then(function (response) {
            const searchData = response.data;
            buildTableData(searchData);
          })
          .catch(function (error) {
            console.log(error);
          });
      } else {
        alert("Please enter at least 2 characters");
      }
    });
  };

  const resetSearch = () => {
    document.getElementById("search-box").value = "";
    getUsers();
  };

  return (
    <div className="maincontainer">
      <h1>Users</h1>

      <form className="UserList_FilterWrapper">
        <input
          className="UserList_SearchBox"
          type="search"
          id="search-box"
          placeholder="Search by Name"
        />
        <input type="reset" onClick={resetSearch} className="UserList_Button" value="Reset" />
      </form>

      <div className="Homepageorders">
        <table className="Homepage_OrderTable">
          <thead>
            <tr>
              <th>ID</th>
              <th>User Avatar</th>
              <th>Full Name</th>
              <th>DoB</th>
              <th>Gender</th>
              <th>Current Location</th>
            </tr>
          </thead>
          <tbody id="mytabledata"></tbody>
        </table>
      </div>
    </div>
  );
};

export default Users;
